           // NOTE: right - left + 1 \u6b64\u5904+1\u662f\u4e3a\u907f\u514d\u4e00\u79cd\u60c5\u51b5\n            // \u5f53left =1\uff0cright =2 \u65f6 left + (right - left) / 2=1;\n            // \u8bbeif\u6761\u4ef6\u8d70\u4e86else\u5206\u652f left\u53c8\u88ab\u8d4b\u503c\u4e3a1\u4e86 \u7136\u540e\u5c31\u6ca1\u73a9\u6ca1\u4e86\u6b21\u5faa\u73af\u4e86\n            int i = left + (right - left + 1) / 2;\n            int j = totalLeft - i;\n            if (nums1[i - 1] > nums2[j]) {\n                // \u4e0b\u4e00\u8f6e\u641c\u7d22\u7684\u533a\u95f4 [left, i - 1]\n                right = i - 1;\n            } else {\n                // \u4e0b\u4e00\u8f6e\u641c\u7d22\u7684\u533a\u95f4 [i, right]\n                left = i;\n            }\n        }\n\n        int i = left;\n        int j = totalLeft - i;\n\n        // NOTE: i \u8868\u793a \u5206\u754c\u7ebf\u6709\u53f3\u8fb9\u7684\u7b2c\u4e00\u4e2a\u5143\u7d20 \u5f53i\u4e3a0\u65f6\u8bf4\u660e\u6570\u7ec4\u5de6\u8fb9\u8d8a\u754c\u5904\u7406\u4e3a\u4e00\u4e2a\u6781\u5c0f\u7684\u503c\n        int nums1LeftMax = i == 0 ? Integer.MIN_VALUE : nums1[i - 1];\n        // NOTE: \u89d2\u6807\u4e3am\u3001n\u65f6\u6570\u7ec4\u5df2\u7ecf\u53f3\u8fb9\u8d8a\u754c \u6b64\u65f6\u5904\u7406\u4e3a\u4e00\u4e2a\u5f88\u5927\u7684\u503c\n        int nums1RightMin = i == m ? Integer.MAX_VALUE : nums1[i];\n        int nums2LeftMax = j == 0 ? Integer.MIN_VALUE : nums2[j - 1];\n        int nums2RightMin = j == n ? Integer.MAX_VALUE : nums2[j];\n\n        if (((m + n) % 2) == 1) {\n            return Math.max(nums1LeftMax, nums2LeftMax);\n        } else {\n            return (double) ((Math.max(nums1LeftMax, nums2LeftMax) + Math.min(nums1RightMin, nums2RightMin))) / 2;\n        }\n    }\n}\n'})}),"\n",(0,i.jsx)(n.h3,{id:"references",children:"References"}),"\n",(0,i.jsxs)(n.ol,{children:["\n",(0,i.jsxs)(n.li,{children:["LEETCODE",(0,i.jsx)(n.a,{href:"https://leetcode-cn.com/problems/median-of-two-sorted-arrays/solution/xun-zhao-liang-ge-you-xu-shu